<script>
  import { onMount, getContext } from "svelte";
  import { stores } from "@sapper/app";
  import { flash } from "../../stores/flashStore";
  import { post, redirectPath } from "utils";
  import Button from "@smui/button";
  import Textfield, { Input, Textarea } from "@smui/textfield";
  import Icon from "@smui/textfield/icon";
  import HelperText from "@smui/textfield/helper-text";
  import { Label } from "@smui/common";

  const { page, session } = stores();
  const adminPassword = "Test@005";
  const adminEmail = "sampleadmin@webpremiere.de";
  const userPassword = "Angela@005";
  const userEmail = "sampleuser@webpremiere.de";
  const { getSnackbar, configSnackbar } = getContext("snackbar");

  let snackbar;
  let errors;
  let message;
  let password = "";
  let email = "";

  $session.user = null;
  $session.role = null;

  onMount(() => {
    snackbar = getSnackbar();
  });

  async function submit(event) {
    let path;
    snackbar.isOpen && snackbar.close();
    // resetAtts();

    const res = await post("auth/login", {
      email,
      password,
    });

    // TODO handle network errors
    errors = res.errors;
    message = res.message || res.data.message || res.statusText;

    if (res.success) {
      flash.update({ message });
      res.data.user &&
        ($session.user = res.data.user) &&
        ($session.role = res.data.user.group.name);
      res.data.groups && ($session.groups = res.data.groups);

      path = redirectPath($page, $session);
      configSnackbar(message, path);
      snackbar.open();
    } else {
      reset();
      configSnackbar(message);
      snackbar.open();
    }
  }

  function reset() {
    email = "";
    password = "";
  }

  function setFields(type) {
    switch (type) {
      case "admin":
        email = adminEmail;
        password = adminPassword;
        break;
      case "user":
        email = userEmail;
        password = userPassword;
    }
  }
</script>

<style>
  .login-grid {
    display: grid;
    grid-gap: 5px;
    grid-row-gap: 0.5rem;
    grid-template-columns: 1fr;
    grid-template-rows: repeat(2, 1fr);
    grid-template-areas:
      "email"
      "pass"
      "button1"
      "button2"
      "button3";
  }

  .email-area {
    grid-area: email;
  }

  .pass-area {
    grid-area: pass;
  }

  .button-area-one {
    grid-area: button1;
  }

  .button-area-two {
    grid-area: button2;
  }

  .button-area-three {
    grid-area: button3;
  }

  @media screen and (min-width: 840px) {
    .login-grid {
      grid-template-areas:
        "email pass"
        "button1 button1"
        "button2 button3";
      grid-template-columns: repeat(2, 1fr);
      grid-template-rows: 2fr 1fr 1fr;
      grid-row-gap: 1rem;
    }

    .button-area-two :global(.login-button:only-child) {
      margin: 0;
    }
    .button-area-two :global(.login-button:nth-child(even)) {
      margin-left: 10px;
    }
    .button-area-two :global(.login-button:nth-child(odd)) {
      margin-right: 10px;
    }
  }
</style>

<form on:submit|preventDefault={submit} method="post">
  <div class="login-grid">
    <span class="email-area lg:mr-2 flex flex-col">
      <Textfield
        variant="outlined"
        withLeadingIcon
        bind:value={email}
        label="Email"
        autocomplete="user-email"
        input$aria-controls="helper-text-outlined-email"
        input$aria-describedby="helper-text-outlined-email">
        <Icon class="material-icons">mail</Icon>
      </Textfield>
      <HelperText id="helper-text-outlined-email">Your Email</HelperText>
    </span>
    <span class="pass-area lg:ml-2 flex flex-col">
      <Textfield
        variant="outlined"
        type="password"
        withTrailingIcon
        bind:value={password}
        label="Password"
        autocomplete="user-password"
        input$aria-controls="helper-text-outlined-password"
        input$aria-describedby="helper-text-outlined-password">
        <Icon class="material-icons">login</Icon>
      </Textfield>
      <HelperText id="helper-text-outlined-password">Your Password</HelperText>
    </span>
    <div class="button-area-one flex">
      <Button
        disabled={!(email && password)}
        class="login-button flex-1"
        type="submit"
        variant="raised">
        <Label>Login</Label>
      </Button>
    </div>
    <div class="button-area-two flex">
      <Button
        on:click={() => setFields('user')}
        color="secondary"
        class="login-button flex-1"
        type="submit"
        variant="raised">
        <Label>Beispiel Benutzer</Label>
      </Button>
    </div>
    <div class="button-area-three flex">
      <Button
        on:click={() => setFields('admin')}
        color="secondary"
        class="login-button flex-1"
        type="submit"
        variant="raised">
        <Label>Beispiel Admin</Label>
      </Button>
    </div>
  </div>
</form>
