<script>
  import { Media, MediaContent } from "@smui/card";
  import Textfield, { Textarea } from "@smui/textfield";
  import { VideoPlayer } from "components/Video";
  import { getExt, getMedia } from "utils";
  import { videoEmitter } from "../../stores/videoEmitter";

  export let media = {};
  export let title = "";
  export let description = "";
  export let isEditMode = false;
  export let user;

  let type = getExt(media.src);
  let paused = true;
  let isPlaying;
  let poster;
  let src;

  $: isPlaying = !paused;

  async function setPosterSrc(id) {
    if (!id) {
      poster = null;
      return;
    }
    let res = await getMedia("IMAGE", id, user, {
      width: 300,
      height: 300,
      square: 0,
    });
    if (res) poster = res;
  }

  async function setVideoSrc(id) {
    let res = await getMedia("VIDEO", id, user, { square: 2 });
    if (res) src = res;
  }

  videoEmitter.subscribe((item) => {
    item.data && item.data.id === media.id && setPosterSrc(media.image_id);
  });

  setPosterSrc(media.image_id);
  setVideoSrc(media.id);
</script>

<style>
  .text-wrapper {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    right: inherit;
    z-index: 1;
    transform: translateX(0);
    transform-origin: 0 center;
    transition-property: transform;
    transition-duration: 0.3s;
    transition-timing-function: ease-in-out;
  }
  .text-wrapper > * {
    color: var(--text-light);
    padding: 5px 17px;
  }
  .editor-wrapper {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 2;
    background: rgba(255, 255, 255, 0.9);
  }
  .editor {
    display: flex;
    flex-direction: column;
    margin: 5px;
  }
  .is-edit-mode {
    display: none;
  }
  .player-container {
    position: relative;
  }
  .is-playing {
    opacity: 0;
    right: 2000px;
    transform: translate(100%);
    transform-origin: 100% center;
    transition-property: opacity, transform;
    transition-duration: 0.1s;
    transition-timing-function: ease-in-out;
  }
</style>

<Media aspectRatio="16x9">
  <MediaContent class="flex" style="display: flex; z-index: 0;">
    <div
      class="text-wrapper bg-gradient-to-b from-black"
      class:isEditMode
      class:is-playing={isPlaying}>
      <h2
        class="mdc-typography--headline6 opacity-25"
        class:opacity-25={!media.title}
        style="margin: 0;">
        {media.title || 'No Title'}
      </h2>
      <h3
        class="mdc-typography--subtitle2 opacity-25"
        class:opacity-25={!media.description}
        style="margin: 0;">
        {media.description || 'No Description'}
      </h3>
    </div>
    <div class="editor-wrapper" class:is-edit-mode={!isEditMode}>
      <div class="editor">
        <Textfield
          class="mb-4"
          variant="outlined"
          dense
          bind:value={title}
          label="Title"
          input$aria-controls="helper-text-title"
          input$aria-describedby="helper-text-title" />
        <Textfield
          textarea
          variant="outlined"
          bind:value={description}
          label="Description"
          input$aria-controls="helper-text-description"
          input$aria-describedby="helper-text-description" />
      </div>
    </div>
    <div class="player-container flex flex-1 bg-black">
      <VideoPlayer
        class="video-player flex"
        bind:paused
        {poster}
        {src}
        {type} />
    </div>
  </MediaContent>
</Media>
